import { CommonModule } from '@angular/common';
import { Component } from '@angular/core';
import { FormsModule } from '@angular/forms';
import { HttpClient } from '@angular/common/http';
import { environment } from '../environments/environment';

interface ChatMessage {
  text: string;
  sender: 'user' | 'bot';
  timestamp: Date;
}

@Component({
  selector: 'app-root',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'Student Support Chatbot';
  userMessage = '';
  sending = false;
  messages: ChatMessage[] = [
    {
      text: 'Salut ! Je suis ton assistant pour les cours, examens et support technique. Comment puis-je t\'aider ? ',
      sender: 'bot',
      timestamp: new Date()
    }
  ];

  quickPrompts: string[] = [
    'Quand est le prochain examen ?',
    'Comment soumettre mon devoir ?',
    'Comment rÃ©initialiser mon mot de passe ?',
    'Qui contacter pour un problÃ¨me technique ?'
  ];

  constructor(private readonly http: HttpClient) {}

  sendPrompt(prompt: string): void {
    this.userMessage = prompt;
    this.sendMessage();
  }

  sendMessage(): void {
    const text = this.userMessage.trim();
    if (!text || this.sending) return;

    const outgoing: ChatMessage = {
      text,
      sender: 'user',
      timestamp: new Date()
    };
    this.messages = [...this.messages, outgoing];
    this.userMessage = '';
    this.sending = true;

    this.http.post<{ reply: string }>(environment.apiUrl, { message: text }).subscribe({
      next: res => {
        const reply: ChatMessage = {
          text: res.reply || 'Je regarde Ã§a pour toi... (aucune rÃ©ponse disponible)',
          sender: 'bot',
          timestamp: new Date()
        };
        this.messages = [...this.messages, reply];
        this.scrollToBottom();
      },
      error: err => {
        const reply: ChatMessage = {
          text: err?.error?.error || 'Impossible de joindre le serveur. RÃ©essaie plus tard.',
          sender: 'bot',
          timestamp: new Date()
        };
        this.messages = [...this.messages, reply];
        this.scrollToBottom();
      },
      complete: () => {
        this.sending = false;
      }
    });
  }

  private scrollToBottom(): void {
    setTimeout(() => {
      const container = document.querySelector('.chat-messages');
      if (container) container.scrollTop = container.scrollHeight;
    }, 0);
  }
}
<div class="page">
  <header class="app-header">
    <div class="logo">ðŸŽ“</div>
    <div>
      <div class="title">{{ title }}</div>
      <div class="status"><span class="dot"></span> En ligne pour vous aider</div>
    </div>
  </header>

  <section class="chat-window">
    <div class="chat-messages">
      <div *ngFor="let message of messages" class="bubble" [ngClass]="message.sender">
        <div class="meta">
          <span>{{ message.sender === 'user' ? 'Vous' : 'Assistant' }}</span>
          <span>{{ message.timestamp | date:'shortTime' }}</span>
        </div>
        <div class="text">{{ message.text }}</div>
      </div>
    </div>

    <div class="quick-row">
      <button *ngFor="let prompt of quickPrompts" class="chip" (click)="sendPrompt(prompt)">{{ prompt }}</button>
    </div>

    <form class="input-row" (ngSubmit)="sendMessage()">
      <input
        [(ngModel)]="userMessage"
        name="message"
        type="text"
        placeholder="Ã‰crivez votre question..."
        [disabled]="sending"
        required
      />
      <button type="submit" [disabled]="sending || !userMessage.trim()">
        {{ sending ? '...' : 'Envoyer' }}
      </button>
    </form>
  </section>
</div>
